from PyQt5 import QtWidgets as qtwfrom namelistView import NamelistViewclass NamelistStack(qtw.QFrame):    def __init__(self, default_par):        super().__init__()        self.default_par = default_par        self.layout = qtw.QStackedLayout()        self.initUI()    def initUI(self):                self.layout.setCurrentIndex(1)                for namelist in self.default_par.namelists:            widget = NamelistView(namelist)            self.layout.addWidget(widget)           self.setLayout(self.layout)