import numpy as npfrom .parameter import ParameterArray, ParameterSingledef dispatch(self, name, value, parent_namelist):    selectedDispatcher = _getDispatcher(value)    return selectedDispatcher(name, value, parent_namelist)def _isArray(value):    if isinstance(value, np.ndarray):        return True    else:        return Falsedef _getDispatcher(value):    if _isArray(value):        return _dispatchArray    else:        return _dispatchSingleVal    def _dispatchSingleVal(name, value, parent_namelist):    dtype = _getdType(value)    return ParameterSingle(dtype, name, value, parent_namelist)def _dispatchArray(name, value, parent_namelist):    dtype = _getdType(value)    return ParameterArray(dtype, name, value, parent_namelist)        def _getdType(value):    v = np.array([value])    if v.dtype.type == np.str_:        if np.any(v == ".true.") or np.any(v == ".false."):            _type = bool        else:            _type = str    else:        v.dtype    return _type